import { useState } from 'react'
import './App.css'

const imagenes = [
    {"src": '/img/img1.jpg'},
    {"src": '/img/img2.png'},
    {"src": '/img/img3.jpg'},
    {"src": '/img/img4.jpg'},
    {"src": '/img/img5.jpg'},
    {"src": '/img/img6.jpg'}

]

function App(){

    const [tarjetas, seTa] = useState([])
    const [turnos, seTu] = useState(0)

    const mezclar = () => {

        const mezcladas = [...imagenes, ...imagenes]
            .sort (() => Math.random() - 0.5)
            .map((tarjeta) => ({...tarjeta, id: Math.random}))
        seTa(mezcladas)
        seTu(0)

    }

    console.log(tarjetas, turnos)
    return(
        <div className='App'>
            <h1>Lab 09</h1>
            <button onClick={mezclar}>Jugar</button> 
            <div className='tablero'>
                {tarjetas.map(tarjeta => (
                    <div className = 'tarjeta' key={tarjeta.id}>
                        <div>
                            <img className = 'enfrente' src={tarjeta.src} alt = 'tarjetaEnfrente'/>
                            <img className = 'atras' src="/img/back.png" alt = 'tarjetaAtras'/>
                        </div>

                    </div>
                ))}    
            </div>    
        </div>
          

    );
}

export default App
