import { useState } from 'react/cjs/react.production.min'
import './App.css'

const imagenes = [
    {"src": '/img/img1.jpg'},
    {"src": '/img/img2.png'},
    {"src": '/img/img3.jpg'},
    {"src": '/img/img4.jpg'},
    {"src": '/img/img5.jpg'},
    {"src": '/img/img6.jpg'}

]

function App(){

    const [tarjetas, seTa] = useState([])
    const [turnos, seTu] = useState(0)

    const mezclar = () => {

        mezcladas = [...imagenes, ...imagenes]
            .sort (()=> Math.random()-0.5)
            .map((tarjeta) => ({...tarjeta, id: Math.random}))
        seTa(mezcladas)
        seTu(0)

    }


    return(
        <div className='App'>
            <h1>Lab 09</h1>
            <button onClick={mezcladas}>Jugar</button> 
        </div>
    );
}

export default App
