import { useState } from 'react'
import './App.css'
import SingleTarjeta from './components/SingleTarjeta.js'

const imagenes = [
    {"src": '/img/img1y.jpg'},
    {"src": '/img/img2y.png'},
    {"src": '/img/img3y.jpg'},
    {"src": '/img/img4.jpg'},
    {"src": '/img/img5y.jpg'},
    {"src": '/img/img6y.jpg'}

]

function App(){

    const [tarjetas, seTa] = useState([])
    const [turnos, seTu] = useState(0)
    const [accion1, setAccion1] = useState(null)
    const [accion2, setAccion2] = useState(null)

    const mezclar = () => {

        const mezcladas = [...imagenes, ...imagenes]
            .sort (() => Math.random() - 0.5)
            .map((tarjeta) => ({...tarjeta, id: Math.random}))
        seTa(mezcladas)
        seTu(0)

    }

    const manejoAccion = (tarjeta) => {
        console.log(tarjeta)

    }

    
    return(
        <div className='App'>
            <h1>Lab 09</h1>
            <button onClick={mezclar}>Jugar</button> 
            <div className='tablero'>
                {tarjetas.map(tarjeta => (
                    <SingleTarjeta key = {tarjeta.id} tarjeta = {tarjeta} manejoAccion = {manejoAccion}/>
                ))}    
            </div>    
        </div>
          

    );
}

export default App
